package ClubHomework;

import java.util.Scanner;

public class GiHyeon2438 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		Scanner sc = new Scanner(System.in);
		
		int a, i, j;
		a = sc.nextInt();
		
		for(i = 0; i < a; i++) {
			for(j=0;j<i+1;j++) {
			System.out.print("*");
			}
			System.out.print("\n");
	}
		
	}

}
